<template>    <div class="row">    <h1>report</h1>    <div class="table-responsive">        <table class="table table-bordered table-striped table-hover" >            <thead>            <tr>                <th>Date</th>                <th>Country</th>                <th>unique Customer</th>                <th>No of Deposit</th>                <th>Total Deposits Amount </th>                <th>No of Withdrawals</th>                <th>Total Withdrawals Amount</th>            </tr>            </thead>            <tbody v-for="record in ReportData">            <td>{{record.theDate}}</td>             <td>{{record.country}}</td>            <td>{{record.id}}</td>            <td>{{record.depositNo}}</td>            <td>{{record.totalDeposit}}</td>            <td>{{record.withdrawNo}}</td>            <td>{{record.totalWithdraw}}</td>            </tbody>        </table>    </div>    </div></template><script>    export default {        data () {            return {             ReportData:{},             currentDate: this.setCurrentDate()                }        },        created() {            this.loadReport();        },       methods: {            loadReport() {               this.$http.get('api/report')                    .then(response => {                        this.ReportData = response.body;                    })            },         setCurrentDate(){            let today = new Date();            let dd = today.getDate();            let mm = today.getMonth()+1;            let yyyy = today.getFullYear();            if(dd<10){                dd='0'+dd;            }            if(mm < 10 ){                mm='0'+mm;            }            return dd+'-'+mm+'-'+yyyy;        }        }    }</script>